import React from "react";
import "./Game.css";
import Row from "./Row";
import TFoot from "./TFoot";
import ButtonRestart from './ButtonRestart'

import { getWinner, initialGame, play, historique, Game} from './game_logique'
import ButtonUndo from "./ButtonUndo";

type GameProps = {};

const GameFinal: React.FC<GameProps> = () => {
  const [game, setGame] = React.useState(initialGame);

  const funcReset = (game: Game) => {
    historique.length = 1
    console.log(historique);
    console.log(historique.length);
    setGame(game);
  }

  return (
    <div className="App">
      <header className="App-header">
        <table>
          <tbody>
            {game.map((row, index) => (
              <Row key={index} row={row} onCellClick={(y: number) => setGame(play(game,index, y))}/>
            ))}
          </tbody>
          <TFoot winner={getWinner(game)}/>
        </table>
        <span>

          <ButtonRestart onButtonClick={() => funcReset(initialGame)}/>
        </span>
      </header>
    </div> 
  );
};

export default GameFinal;
