import React from "react";
import "./App.css";
import Row, { RowProps } from "./Row";
import TFoot from "./TFoot";
import ButtonRestart from './ButtonRestart'

import { getWinner, initialGame } from './game_logique'



type GameProps = {
  myGame: RowProps["row"][];
  onRowClick: RowProps["onCellClick"];
};

const Game: React.FC<GameProps> = ({myGame, onRowClick}) => {
  const [game, setGame] = React.useState(initialGame);
  return (
    <table>
      <tbody>
        {myGame.map((row, index) => (
          <Row key={index} row={row} onCellClick={onRowClick}/>
        ))}
      </tbody>
      <TFoot winner={getWinner(initialGame)}/>
    </table>
  );
};

const App: React.FC = () => {
    return (
      <div className="App">
            <header className="App-header">
                <Game />
                <ButtonRestart onClick={() => {}}/>
            </header>
        </div>    
  );
};

export default App;
