import React from "react";
import "./App.css";
import Row, { RowProps } from "./Row";
import TFoot from "./TFoot";
import ButtonRestart from './ButtonRestart'

import { getWinner, initialGame, play } from './game_logique'



type GameProps = {
  //myGame: RowProps["row"][];
  onRowClick: (x: number, y: number) => void;
};

const Game: React.FC<GameProps> = ({onRowClick}) => {
  const [game, setGame] = React.useState(initialGame);

  return (
    <table>
      <tbody>
        {game.map((row, index) => (
          <Row key={index} row={row} onCellClick={(y: number) => onRowClick(index, y)}/>
        ))}
      </tbody>
      <TFoot winner={getWinner(initialGame)}/>
    </table>
  );
};

const App: React.FC = () => {
    return (
      <div className="App">
            <header className="App-header">
                <Game onRowClick={(x: number, y: number) => play(initialGame, x, y)}/>
                <ButtonRestart onClick={() => {}}/>
            </header>
        </div>    
  );
};

export default App;
