import React from "react";
import "./App.css";
import Row, { RowProps } from "./Row";
import TFoot from "./TFoot";
import ButtonRestart from './ButtonRestart'

import { getWinner, initialGame, play } from './game_logique'
import Cell from "./Cell";



type GameProps = {
  myGame: RowProps["row"][];
  onClick: RowProps["onCellClick"];
};

const Game: React.FC<GameProps> = ({myGame, onlick}) => {
  //const [game, setGame] = React.useState(initialGame);
  return (
    <table>
      <tbody>
        {myGame.map((row, index) => (
          <Row key={index} row={row} onCellClick={onClick}/>
        ))}
      </tbody>
      <TFoot winner={getWinner(initialGame)}/>
    </table>
  );
};

const App: React.FC = () => {
    return (
      <div className="App">
            <header className="App-header">
                <Game myGame={initialGame} onRowClick={() => play(initialGame, 1, 1)}/>
                <ButtonRestart onClick={() => {}}/>
            </header>
        </div>    
  );
};

export default App;
