import React from "react";
import "./Game.css";
import Row from "./Row";
import TFoot from "./TFoot";
import ButtonRestart from './ButtonRestart'

import { getWinner, initialGame, play, historique} from './game_logique'
import ButtonUndo from "./ButtonUndo";

type GameProps = {};

const Game: React.FC<GameProps> = () => {
  const [game, setGame] = React.useState(initialGame);

  const funcReset = () => {
    historique.length = 1;
    setGame(initialGame);
  }

  return (
    <div className="App">
      <header className="App-header">
        <table>
          <tbody>
            {game.map((row, index) => (
              <Row key={index} row={row} onCellClick={(y: number) => setGame(play(game,index, y))}/>
            ))}
          </tbody>
          <TFoot winner={getWinner(game)}/>
        </table>
        <span>
          <ButtonUndo onButtonClick={() => (setGame(historique[historique.length - 2]))}/>
          <ButtonRestart onButtonClick={() => funcReset()}/>
        </span>
      </header>
    </div> 
  );
};

export default Game;
