import React from "react";
import "./App.css";
import Row, { RowProps } from "./Row";
import TFoot from "./TFoot";
import ButtonRestart from './ButtonRestart'

import { getWinner, initialGame } from './game_logique'



type GameProps = {
  onClick: RowProps["onCellClick"];
  game: RowProps["row"][];
};

const Game: React.FC<GameProps> = gameProps => {
  return (
    <table>
      <tbody>
        {gameProps.game.map((row, index) => (
          <Row row={row} onClick={() => {}} indexRow={index}/>
        ))}
      </tbody>
      <TFoot winner={getWinner(initialGame)}/>
    </table>
  );
};

const App: React.FC = () => {
    return (
      <div className="App">
            <header className="App-header">
                <Game />
                <ButtonRestart onClick={() => {}}/>
            </header>
        </div>

  );
};

export default App;
